        <script>


            function menu_height () {
                var winHeight = $(window).height();
                var menuHeight = $('.newheader').outerHeight();
                var sideMenuHeight = winHeight - menuHeight;
                $('.left_toggle, .right_toggle').height(sideMenuHeight).css('top', 'menuHeight');
            }

            menu_height();

            $(this).resize(function () {
                menu_height();
            })

            var menuHeight = $('.newheader').outerHeight();

            function aside_left_on() {
                $(".left_toggle").addClass("left_on").css("top", "menuHeight");
                $("#wrap").height($(window).height()).css({overflow: "hidden"});
                $(".tg_on").attr("src", "/images/v2/toggle_menu_close.png");
                $("#toggle_left_status").val(1);
            }

            function aside_left_off() {
                $('.left_toggle').removeClass('left_on')
                $('.tg_on').attr('src', '/images/v2/toggle_menu.png');
                $('#toggle_left_status').val(0);
            }

            function aside_right_on() {
                $('.right_toggle').addClass('right_on').css('top', 'menuHeight');;
                $('.myinfo_on').attr('src', '/images/v2/myinfo_menu_close.png');
                $('#toggle_right_status').val(1);
            }

            function aside_right_off() {
                $('.right_toggle').removeClass('right_on');
                $('.myinfo_on').attr('src', '/images/v2/myinfo.png');
                $('#toggle_right_status').val(0);
            }

            function menu_on_wrap() {
                var fullHeight = $(window).height();
                $('#wrap').height(fullHeight).css({overflow: 'hidden'});
            }

            function menu_off_wrap() {
                $('#wrap').removeAttr('style');
            }

            $('#toggle_left').click(function() {
                if ($('#toggle_left_status').val() == 0) {
                    aside_left_on();
                    menu_on_wrap();
                    if ($('#toggle_right_status').val() == 1) {
                        aside_right_off ();
                    }
                } else {
                    aside_left_off();
                    menu_off_wrap();
                }
            });

            $('#toggle_right').click(function() {
                if ($('#toggle_right_status').val() == 0) {
                    aside_right_on();
                    menu_on_wrap();
                    if ($('#toggle_left_status').val() == 1) {
                        aside_left_off();
                    }
                } else {
                    aside_right_off ();
                    menu_off_wrap();
                };
            });



        </script>

