                        <script type="text/javascript">
                            var art_max_width = 640;
                            /* 폰트값 쿠키 이용하여 고정하기 */
                            function news_large()
                            {
                                var art_font_size = parseInt($('.cnt_view').css('fontSize'));
                                var art_line_height = parseInt($('.cnt_view').css('lineHeight'));
                                art_font_size++;
                                art_line_height += 1;
                                if (art_font_size > 20)
                                    art_font_size = 20;
                                if (art_line_height > 29)
                                    art_line_height = 29;
                                set_cookie("SMALL_FONT_M", "" , 0);
                                set_cookie("SMALL_FONT_HEIGHT_M", "" , 0);
                                news_reset_font(art_font_size,art_line_height);
                            }
                            function news_small()
                            {
                                var art_font_size = parseInt($('.cnt_view').css('fontSize'));
                                var art_line_height = parseInt($('.cnt_view').css('lineHeight'));
                                art_font_size--;
                                art_line_height -= 1;
                                if (art_font_size < 13)
                                    art_font_size = 13;
                                if (art_line_height < 22)
                                    art_line_height = 22;
                                set_cookie("LARGE_FONT_M", "" , 0);
                                set_cookie("LARGE_FONT_HEIGHT_M", "" , 0);
                                news_reset_font(art_font_size,art_line_height);
                            }
                            function news_reset_font(art_font_size,art_line_height)
                            {
                                $('.cnt_view').css('fontSize', art_font_size+'px');
                                $('.cnt_view').css('lineHeight', art_line_height+'px');
                                if(art_font_size > 16) {
                                    if(getCookie("LARGE_FONT_M")){
                                        set_cookie("LARGE_FONT_M", "" , 0);
                                        set_cookie("LARGE_FONT_HEIGHT_M", "" , 0);
                                        set_cookie("LARGE_FONT_M", art_font_size , 1);
                                        set_cookie("LARGE_FONT_HEIGHT_M", art_line_height , 1);
                                    } else {
                                        set_cookie("LARGE_FONT_M", art_font_size , 1);
                                        set_cookie("LARGE_FONT_HEIGHT_M", art_line_height , 1);
                                    }
                                }
                                if(art_font_size < 16) {
                                    if(getCookie("SMALL_FONT_M")) {
                                        set_cookie("SMALL_FONT_M", "" , 0);
                                        set_cookie("SMALL_FONT_HEIGHT_M", "" , 0);
                                        set_cookie("SMALL_FONT_M", art_font_size , 1);
                                        set_cookie("SMALL_FONT_HEIGHT_M", art_line_height , 1);
                                    } else {
                                        set_cookie("SMALL_FONT_M", art_font_size , 1);
                                        set_cookie("SMALL_FONT_HEIGHT_M", art_line_height , 1);
                                    }
                                }
                            }
                            function checkFontCookie(){
                                if(getCookie("LARGE_FONT_M")){
                                    $('.cnt_view').css('fontSize', getCookie("LARGE_FONT_M")+'px');
                                    $('.cnt_view').css('lineHeight', getCookie("LARGE_FONT_HEIGHT_M")+'px');
                                }
                                if(getCookie("SMALL_FONT_M")) {
                                    $('.cnt_view').css('fontSize', getCookie("SMALL_FONT_M")+'px');
                                    $('.cnt_view').css('lineHeight', getCookie("SMALL_FONT_HEIGHT_M")+'px');
                                }
                            }
                            /* 폰트값 쿠키 이용하여 고정하기 */
                            function go_top(orix,oriy,desx,desy) {
                                var Timer;
                                if (document.body.scrollTop == 0) {
                                    var winHeight = document.documentElement.scrollTop;
                                } else {
                                    var winHeight = document.body.scrollTop;
                                }
                                if(Timer) clearTimeout(Timer);
                                startx = 0;
                                starty = winHeight;
                                if(!orix || orix < 0) orix = 0;
                                if(!oriy || oriy < 0) oriy = 0;
                                var speed = 7;
                                if(!desx) desx = 0 + startx;
                                if(!desy) desy = 0 + starty;
                                desx += (orix - startx) / speed;
                                if (desx < 0) desx = 0;
                                desy += (oriy - starty) / speed;
                                if (desy < 0) desy = 0;
                                var posX = Math.ceil(desx);
                                var posY = Math.ceil(desy);
                                window.scrollTo(posX, posY);
                                if((Math.floor(Math.abs(startx - orix)) < 1) && (Math.floor(Math.abs(starty - oriy)) < 1)){
                                    clearTimeout(Timer);
                                    window.scroll(orix,oriy);
                                }else if(posX != orix || posY != oriy){
                                    Timer = setTimeout("go_top("+orix+","+oriy+","+desx+","+desy+")",15);
                                }else{
                                    clearTimeout(Timer);
                                }
                            }
                        </script>
